/***
 * This function maps the intents to it's wellnes goals products and extracts parameters to filter results.
 * @param intent
 * @param params
 */

exports.getResponses = (intent, params)=>{
       let payload = {};

       if (intent === 'energyRich'){
              payload = energyRich;
       }
       if(intent === 'beautyAndSkinRich'){
              payload = beautyAndSkinRich;
       }
       if(intent === 'boneHealthRich'){
              payload = boneHealthRich;
       }
       if(intent === 'digestiveRich'){
              payload = digestiveRich;
       }
       if(intent === 'healthyAging'){
              payload = healthyAging;
       }
       if(intent === 'immuneRich'){
              payload = immuneRich;
       }
       if(intent === 'kidsHealthRich'){
              payload = kidsHealthRich;
       }
       if(intent === 'prenatalPostnatalRich'){
              payload = prenatalPostnatalRich;
       }
       if(intent === 'sleepStressRich'){
              payload = sleepStressRich;
       }
       if(intent === 'sportRich'){
              payload = sportRich;
       }
       let items = payload.richResponse.items[1].carouselBrowse.items;

       if(params.hasOwnProperty('user-gender')){

              payload.richResponse.items[1].carouselBrowse.items = filterParameter(items,'user-gender',params['user-gender']);
       }
       if(params.hasOwnProperty('age')){
              if(params.age.amount < 40 && params['user-gender'] === 'male'){
               payload = mensHealth;
              }
              if(params.age.amount < 40 && params['user-gender'] === 'female'){
                     payload = womensHealth;
              }
       }

       return payload;
};

function filterParameter (payload, param, value) {
        return payload.filter(item => item.parameters[param] === value || item.parameters[param] === undefined);
}
